import logo from './logo.svg';
import './App.css';
import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchBookData } from './store/book-actions';
import { bookActions } from './store/book-slice';

function App() {
  const dispatch = useDispatch();
  const showCart = useSelector((state) => state.ui.cartIsVisible);
  const book = useSelector((state) => state.book);
  const notification = useSelector((state) => state.ui.notification);
  const addItemHandler = () => {
    dispatch(
      bookActions.nextPage()
    );
  };

  const addItemHandler2 = () => {
    dispatch(
      bookActions.previousPage()
    );
  };

  useEffect(() => {
    dispatch(fetchBookData(book.url));

  }, [book.url,dispatch]);

  console.log(book)

  return (
    <div className="App">

      {book.books.map(b=><span>{b.id} </span>)}
      <button onClick={addItemHandler}>+</button>
      <button onClick={addItemHandler2}>-</button>
    </div>
  );
}

export default App;
