import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchBookData } from './store/book-actions';
import { bookActions } from './store/book-slice';

function App() {
  const dispatch = useDispatch();
  const showCart = useSelector((state) => state.ui.cartIsVisible);
  const book = useSelector((state) => state.book);
  const notification = useSelector((state) => state.ui.notification);
  const addItemHandler = () => {
    dispatch(
      bookActions.nextPage()
    );
  };

  const addItemHandler2 = () => {
    dispatch(
      bookActions.previousPage()
    );
  };

  useEffect(() => {
    dispatch(fetchBookData(book.url));

  }, [book.url,dispatch]);

  console.log(book.url);

  return (
    <div className="bg-white">
      <div className="max-w-2xl mx-auto py-16 px-4 sm:py-24 sm:px-6 lg:max-w-7xl lg:px-8">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900">Popular Now</h2>

        <div className="mt-6 grid grid-cols-2 gap-y-10 gap-x-6 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:gap-x-8">
          {book.books.map((book) => (
            <div key={book.id} className="group relative">
              <div className="max-w-10 h-80 bg-gray-200 aspect-w-1 aspect-h-1 rounded-md overflow-hidden group-hover:opacity-75 lg:h-80 lg:aspect-none">
                <img
                  src={book.formats["image/jpeg"]}
                  alt={book.subjects}
                  className="w-full h-full object-center object-fill lg:w-full lg:h-full"
                />
              </div>
              <div className="mt-4 flex justify-between">
                <div>
                  <h3 className="text-sm text-gray-700">
                    <a>
                      <span aria-hidden="true" className="absolute inset-0" />
                      {book.title}
                    </a>
                  </h3>
                  <p className="mt-1 text-sm text-gray-500">{book.authors.name}</p>
                </div>
                <p className="text-sm font-medium text-gray-900">{book.download_count}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
