import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchBookData } from '../../store/book-actions';
import { bookActions } from '../../store/book-slice';
import BookItem from './BookItem';

const Books = () =>{
    const dispatch = useDispatch();
    const book = useSelector((state) => state.book);

    useEffect(() => {
        dispatch(fetchBookData(book.url));
      }, [book.url,dispatch]);

      const addItemHandler = () => {
        dispatch(
          bookActions.nextPage()
        );
      };
    
      const addItemHandler2 = () => {
        dispatch(
          bookActions.previousPage()
        );
      };

      

    return(
    <div className="bg-white">
      <div className="max-w-2xl mx-auto py-16 px-4 sm:py-24 sm:px-6 lg:max-w-7xl lg:px-8">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900">Popular Now</h2>
        <div className="mt-6 grid grid-cols-2 gap-y-10 gap-x-6 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:gap-x-8">
        <div class="border border-blue-300 shadow rounded-md p-4 max-w-sm w-full mx-auto">
  <div class="animate-pulse flex space-x-4">
    <div class="rounded-full bg-slate-700 h-10 w-10"></div>
    <div class="flex-1 space-y-6 py-1">
      <div class="h-2 bg-slate-700 rounded"></div>
      <div class="space-y-3">
        <div class="grid grid-cols-3 gap-4">
          <div class="h-2 bg-slate-700 rounded col-span-2"></div>
          <div class="h-2 bg-slate-700 rounded col-span-1"></div>
        </div>
        <div class="h-2 bg-slate-700 rounded"></div>
      </div>
    </div>
  </div>
</div>
          
          {book.books.map((book) => (<BookItem key={book.id}  book={book} />))}
        </div>
      </div>
    </div>
    );
};

export default Books;
