import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchBookData } from '../../store/book-actions';
import { bookActions } from '../../store/book-slice';

const Books = () =>{
    const dispatch = useDispatch();
    const book = useSelector((state) => state.book);

    useEffect(() => {
        dispatch(fetchBookData(book.url));
        console.log(book.url)
      }, [book.url,dispatch]);

      const addItemHandler = () => {
        dispatch(
          bookActions.nextPage()
        );
      };
    
      const addItemHandler2 = () => {
        dispatch(
          bookActions.previousPage()
        );
      };

      

    return(
    <div className="bg-white">
      <div className="max-w-2xl mx-auto py-16 px-4 sm:py-24 sm:px-6 lg:max-w-7xl lg:px-8">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900">Popular Now</h2>

        <div className="mt-6 grid grid-cols-2 gap-y-10 gap-x-6 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:gap-x-8">
          {book.books.map((book) => (
            <div key={book.id} className="group relative">
              <div className="max-w-[17rem] h-80 bg-gray-200 aspect-w-1 aspect-h-1 rounded-md overflow-hidden group-hover:opacity-75 lg:h-80 lg:aspect-none">
                <img
                  src={book.formats["image/jpeg"]}
                  alt={book.subjects}
                  className="w-full h-full object-center object-fill lg:w-full lg:h-full"
                />
              </div>
              <div className="max-w-[17rem] mt-4 flex justify-between">
                
                <span className="text-sm text-gray-700">{book.title}</span>
                
                <span className="text-sm font-medium text-gray-900">{book.download_count}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
    );
};

export default Books;
