import { uiActions } from './ui-slice';
import { cartActions } from './cart-slice';

export const fetchCartData = (url = null) => {
  return async (dispatch) => {
    const fetchData = async () => {
      const response = await fetch(
        url == null?'http://gutendex.com/books' : url
      );

      if (!response.ok) {
        throw new Error('Could not fetch books data!');
      }

      const data = await response.json();

      return data;
    };

    try {
      const bookData = await fetchData();
      dispatch(
        cartActions.replaceCart({
          books: bookData.results || [],
          nextPage: bookData.next,
          previousPage: bookData.previous,
        })
      );
    } catch (error) {
      dispatch(
        uiActions.showNotification({
          status: 'error',
          title: 'Error!',
          message: 'Fetching books data failed!',
        })
      );
    }
  };
};
